!> Compute a given derivative of the onsite coulomb integral
!!
!!                               //
!! (d/du1)^n(u) * (d/dv2)^m(v) * || exp(a*|r-r1|^2) * 1/|r-r'| * exp(b*|r-r2|^2) dr * dr' 
!!                               //
!!
!! when r2 -> r1
!!
recursive function D_X_D(il,ir,x,y,z,q,p,d,erfpd,exppd)

  implicit none

  ! input arguments
  integer     , intent(in):: il!< index giving left orders of derivation
  integer     , intent(in):: ir!< index giving right orders of derivation
  real(kind=8), intent(in):: q!< combined exponent = a*b/(a+b)
  real(kind=8), intent(in):: x!< rr-rl x component
  real(kind=8), intent(in):: y!< rr-rl y component
  real(kind=8), intent(in):: z!< rr-rl z component
  real(kind=8), intent(in), optional:: p!< sqrt(q)
  real(kind=8), intent(in), optional:: d!< sqrt(x^2+y^2+z^2)
  real(kind=8), intent(in), optional:: erfpd!< erf(p*d)
  real(kind=8), intent(in), optional:: exppd!< exp(-p^2*d^2)
 
  ! return value
  real(kind=8) D_X_D

  ! parameter
  real(kind=8), parameter :: PI=3.1415926535897932384626433832795d0

  ! local variables
  integer                 :: ndu
  integer                 :: ndv
  integer                 :: ndw
  integer                 :: ndxl
  integer                 :: ndxr
  integer                 :: ndyl
  integer                 :: ndyr
  integer                 :: ndzl
  integer                 :: ndzr
  real(kind=8)            :: u
  real(kind=8)            :: v
  real(kind=8)            :: w
  real(kind=8)            :: s 
  real(kind=8)            :: x_
  real(kind=8)            :: y_
  real(kind=8)            :: z_
  real(kind=8)            :: p_
  real(kind=8)            :: d_
  real(kind=8)            :: erfpd_
  real(kind=8)            :: exppd_
  real(kind=8)            :: pd
  real(kind=8)            :: pd2

  ! get derivation indices
  ndxl=ndx_from_ir(il)
  ndxr=ndx_from_ir(ir)
  ndyl=ndy_from_ir(il)
  ndyr=ndy_from_ir(ir)
  ndzl=ndz_from_ir(il)
  ndzr=ndz_from_ir(ir)
  
  ! sort coefficients
  if(ndxl+ndxr.ge.ndyl+ndyr)then
    if(ndyl+ndyr.ge.ndzl+ndzr)then
      ! case x >= y >= z
      u=x
      v=y
      w=z
      ndu=ndxl+ndxr
      ndv=ndyl+ndyr
      ndw=ndzl+ndzr
    else
      if(ndxl+ndxr.ge.ndzl+ndzr)then
        ! case x >= z >= y
        u=x
        v=z
        w=y
        ndu=ndxl+ndxr
        ndv=ndzl+ndzr
        ndw=ndyl+ndyr
      else
        ! case z >= x >= y
        u=z
        v=x
        w=y
        ndu=ndzl+ndzr
        ndv=ndxl+ndxr
        ndw=ndyl+ndyr
      end if
    end if
  else
    if(ndxl+ndxr.ge.ndzl+ndzr)then
      ! case y >= x >= z
      u=y
      v=x
      w=z
      ndu=ndyl+ndyr
      ndv=ndxl+ndxr
      ndw=ndzl+ndzr
    else
      if(ndyl+ndyr.ge.ndzl+ndzr)then
        ! case y >= z >= x
        u=y
        v=z
        w=x
        ndu=ndyl+ndyr
        ndv=ndzl+ndzr
        ndw=ndxl+ndxr
      else
        ! case z >= y >= x
        u=z
        v=y
        w=x
        ndu=ndzl+ndzr
        ndv=ndyl+ndyr
        ndw=ndxl+ndxr
      end if
    end if
  end if

  ! get interaction sign
  s=(-1.0d0)**modulo(ndxl+ndyl+ndzl,2)

  ! compute d
  if ( present(d) ) then
    d_=d
  else
    d_=sqrt(x**2+y**2+z**2)
  end if

  ! norm components
  x_=0.0d0
  y_=0.0d0
  z_=0.0d0
  if(d_>0)then
    x_=u/d_
    y_=v/d_
    z_=w/d_
  end if

  ! compute p
  if ( present(p) ) then
    p_=p
  else
    p_=sqrt(q)
  end if

  ! compute pd
  pd=p_*d_

  ! compute pd2
  pd2=pd**2

  ! compute erf(p*d)  
  if ( present(erfpd) ) then
    erfpd_=erfpd
  else
    erfpd_=erf(pd)
  end if  

  ! compute exp(p*d)  
  if ( present(exppd) ) then
    exppd_=exppd
  else
    exppd_=exp(-pd2)
  end if

  ! get interaction value
  D_X_D=s*D_X_D_utils(x_,y_,z_,d_,p_,pd,pd2,erfpd_,exppd_,ndu,ndv,ndw)

end function


